"use client";

import { motion, AnimatePresence } from "framer-motion";
import { BriefingWizard } from "@/components/briefing";
import LeafShadowOverlay from "@/components/ui/LeafShadowOverlay";
import Image from "next/image";
import { useState, useEffect } from "react";

export default function BriefingPage() {
    const [currentImage, setCurrentImage] = useState(0);

    // Background images carousel
    const images = [
        "/images/assets/couple-beach.png",
        "/images/assets/couple-garden.png"
    ];

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentImage((prev) => (prev + 1) % images.length);
        }, 5000); // Change every 5s
        return () => clearInterval(timer);
    }, [images.length]);

    return (
        <main className="min-h-screen w-full relative bg-[#F7F5F0] overflow-x-hidden selection:bg-[#C19B58] selection:text-white flex flex-col items-center">
            <LeafShadowOverlay />

            <div className="w-full max-w-7xl mx-auto grid lg:grid-cols-2 gap-8 lg:gap-16 relative z-10 px-6 lg:px-12 items-start">

                {/* LEFT COLUMN: FORM */}
                <div className="order-2 lg:order-1 flex flex-col py-8 lg:py-16">
                    <div className="mb-8 text-center lg:text-left transition-all">
                        <p className="text-[#C19B58] text-[10px] md:text-xs font-bold uppercase tracking-[0.2em] mb-1 md:mb-2">Conciergerie Digital</p>
                        <h1 className="text-3xl md:text-4xl text-[#2A3B2E] font-[family-name:var(--font-heading)] leading-tight">
                            Personalize seu Dia
                        </h1>
                    </div>

                    <div className="w-full">
                        <BriefingWizard />
                    </div>

                    <div className="mt-8 text-center lg:text-left">
                        <p className="text-[10px] text-[#6B7A6C] uppercase tracking-widest opacity-60 flex items-center gap-2 justify-center lg:justify-start">
                            <span className="w-1.5 h-1.5 rounded-full bg-[#C19B58] animate-pulse" />
                            Atendimento via WhatsApp
                        </p>
                    </div>
                </div>

                {/* RIGHT COLUMN: IMAGE CARD (Sticky + Top Aligned) */}
                {/* sticky top-12 puts it 3rem from top, matching roughly the py-16 of the left col */}
                <div className="hidden lg:flex order-1 lg:order-2 sticky top-6 h-auto min-h-screen items-start py-16 justify-center">
                    <div className="relative w-full max-w-sm xl:max-w-md aspect-[3/4] rounded-2xl overflow-hidden shadow-2xl border-[8px] border-white transition-all duration-500 hover:scale-[1.02] hover:shadow-3xl">
                        <AnimatePresence mode="wait">
                            <motion.div
                                key={currentImage}
                                initial={{ opacity: 0, scale: 1.05 }}
                                animate={{ opacity: 1, scale: 1 }}
                                exit={{ opacity: 0 }}
                                transition={{ duration: 2 }}
                                className="absolute inset-0"
                            >
                                <Image
                                    src={images[currentImage]}
                                    alt="Casal LUMA"
                                    fill
                                    className="object-cover"
                                    priority
                                />
                            </motion.div>
                        </AnimatePresence>

                        <div className="absolute inset-0 bg-gradient-to-t from-[#2A3B2E]/60 via-transparent to-transparent opacity-80" />

                        <div className="absolute bottom-8 left-8 right-8 text-center text-white">
                            <p className="font-[family-name:var(--font-heading)] text-2xl italic mb-1 drop-shadow-md">
                                "O luxo est├í nos detalhes."
                            </p>
                        </div>
                    </div>

                    {/* Decorative Blurs (Adjusted positions) */}
                    <div className="absolute -z-10 top-[10%] right-[0%] w-32 h-32 bg-[#C19B58]/20 rounded-full blur-3xl animate-pulse" />
                    <div className="absolute -z-10 bottom-[30%] left-[0%] w-40 h-40 bg-[#2A3B2E]/10 rounded-full blur-3xl" />
                </div>
            </div>
        </main>
    );
}

"use client";

import EditorSidebar from "@/components/editor/EditorSidebar";
import { ArrowLeft, Monitor, Smartphone, ExternalLink } from "lucide-react";
import { useState, useRef, useEffect } from "react";
import { TemplateData, defaultTemplateData } from "@/types/template";
import Link from 'next/link';

export default function EditorPage() {
    const [viewMode, setViewMode] = useState<"desktop" | "mobile">("desktop");
    const [data, setData] = useState<TemplateData>(defaultTemplateData);
    const iframeRef = useRef<HTMLIFrameElement>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);
    const activeImageField = useRef<string | null>(null);

    const handleDataChange = (field: keyof TemplateData | string, value: any) => {
        setData(prev => {
            if (field.includes('.')) {
                const [parent, child] = field.split('.');
                return {
                    ...prev,
                    [parent]: {
                        ...(prev as any)[parent],
                        [child]: value
                    }
                };
            }
            return { ...prev, [field]: value };
        });
    };

    // Handle messages from iframe (EditableText updates, Image clicks)
    useEffect(() => {
        const handleMessage = (event: MessageEvent) => {
            // In prod verify origin
            if (!event.data) return;

            if (event.data.type === 'UPDATE_DATA') {
                const { field, value } = event.data;
                if (field && value !== undefined) {
                    setData(prev => ({ ...prev, [field]: value }));
                }
            }

            if (event.data.type === 'REQUEST_IMAGE_CHANGE') {
                const { field } = event.data;
                activeImageField.current = field;
                fileInputRef.current?.click();
            }
        };

        window.addEventListener('message', handleMessage);
        return () => window.removeEventListener('message', handleMessage);
    }, []);

    // Sync data with iframe whenever it changes
    useEffect(() => {
        if (iframeRef.current?.contentWindow) {
            iframeRef.current.contentWindow.postMessage({ type: 'UPDATE_DATA', payload: data }, '*');
        }
    }, [data]);

    const handleIframeLoad = () => {
        if (iframeRef.current?.contentWindow) {
            iframeRef.current.contentWindow.postMessage({ type: 'UPDATE_DATA', payload: data }, '*');
        }
    };

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file && activeImageField.current) {
            const imageUrl = URL.createObjectURL(file);
            handleDataChange(activeImageField.current as keyof TemplateData, imageUrl);
            // Reset input
            e.target.value = '';
        }
    };

    return (
        <div className="flex h-screen bg-[#F7F5F0] overflow-hidden font-sans">
            {/* Hidden File Input for Image Uploads */}
            <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                className="hidden"
                accept="image/*"
            />

            {/* LEFT SIDEBAR - EDITOR */}
            <div className="w-[400px] flex-shrink-0 bg-[#F7F5F0] border-r border-[#DCD3C5] z-20 shadow-xl relative">
                <EditorSidebar data={data} onChange={handleDataChange} />

                {/* Back Link */}
                <Link
                    href="/dashboard"
                    className="absolute top-6 right-6 p-2 rounded-full hover:bg-[#E5E0D6] text-[#6B7A6C] transition-colors"
                    title="Voltar ao Dashboard"
                >
                    <ArrowLeft size={20} />
                </Link>
            </div>

            {/* RIGHT AREA - PREVIEW */}
            <div className="flex-1 flex flex-col h-full relative bg-[#E5E0D6]/30">

                {/* Toolbar */}
                <div className="h-14 bg-white/80 border-b border-[#DCD3C5] flex items-center justify-center gap-4 px-6 z-10 backdrop-blur-sm">
                    <div className="flex items-center bg-[#F7F5F0] rounded-lg p-1 border border-[#DCD3C5]">
                        <button
                            onClick={() => setViewMode("desktop")}
                            className={`p-2 rounded-md transition-all ${viewMode === 'desktop' ? 'bg-white shadow-sm text-[#2A3B2E]' : 'text-[#6B7A6C] hover:text-[#2A3B2E]'}`}
                        >
                            <Monitor size={18} />
                        </button>
                        <button
                            onClick={() => setViewMode("mobile")}
                            className={`p-2 rounded-md transition-all ${viewMode === 'mobile' ? 'bg-white shadow-sm text-[#2A3B2E]' : 'text-[#6B7A6C] hover:text-[#2A3B2E]'}`}
                        >
                            <Smartphone size={18} />
                        </button>
                    </div>

                    <div className="absolute right-6 flex items-center gap-2">
                        <a href="/preview/template1" target="_blank" className="text-xs font-medium text-[#6B7A6C] hover:text-[#C19B58] flex items-center gap-1 transition-colors">
                            Ver ao vivo <ExternalLink size={12} />
                        </a>
                    </div>
                </div>

                {/* Preview Container */}
                <div className="flex-1 overflow-hidden flex items-center justify-center p-8 bg-noise">
                    <div
                        className={`transition-all duration-500 bg-white shadow-2xl overflow-hidden origin-center ${viewMode === 'mobile'
                            ? 'w-[375px] h-[812px] rounded-[3rem] border-[8px] border-[#2A3B2E] ring-4 ring-[#C19B58]/20 shadow-[#2A3B2E]/20'
                            : 'w-full h-full rounded-lg border border-[#DCD3C5]'
                            }`}
                    >
                        <iframe
                            ref={iframeRef}
                            src="/preview/template1"
                            className="w-full h-full bg-white"
                            onLoad={handleIframeLoad}
                            title="Preview do Site"
                        />
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { motion } from "framer-motion";
import { ArrowLeft, ArrowRight, CheckCircle, Sparkles } from "lucide-react";
import { useState } from "react";
import { useRouter } from "next/navigation";
import Image from "next/image";
import LeafShadowOverlay from "@/components/ui/LeafShadowOverlay";

export default function LoginPage() {
    const [isLogin, setIsLogin] = useState(false); // Default to REGISTER (Start Project)
    const router = useRouter();

    const handleAuth = (e: React.FormEvent) => {
        e.preventDefault();
        // In a real app, validation and auth logic here
        router.push('/briefing');
    };

    return (
        // Added h-[100dvh] and overflow-hidden to prevent page scroll
        <div className="h-[100dvh] w-full grid lg:grid-cols-2 bg-[#F7F5F0] overflow-hidden">
            {/* Left Column - Brand/Visual */}
            <div className="relative hidden lg:flex h-full overflow-hidden bg-[#2A3B2E] flex-col justify-between p-12 xl:p-16 text-[#F7F5F0]">
                {/* Texture Overlay */}
                <div
                    className="absolute inset-0 opacity-20 mix-blend-multiply"
                    style={{
                        backgroundImage: 'url(/images/assets/noise-texture.png)',
                        backgroundRepeat: 'repeat',
                        backgroundSize: '300px'
                    }}
                />

                {/* Organic Overlay */}
                <div className="absolute inset-0 bg-gradient-to-b from-black/20 to-transparent pointer-events-none" />

                <motion.div
                    className="relative z-10"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.2 }}
                >
                    <a href="/" className="inline-flex items-center gap-2 group mb-8 opacity-70 hover:opacity-100 transition-opacity text-[#C19B58]">
                        <ArrowLeft size={20} className="group-hover:-translate-x-1 transition-transform" />
                        <span>Voltar para Home</span>
                    </a>
                    <h1 className="text-4xl xl:text-5xl font-medium mb-4 xl:mb-6 font-[family-name:var(--font-heading)] leading-tight">
                        Vamos planejar o <br />
                        <span className="text-[#C19B58] italic">grande dia?</span>
                    </h1>
                    <p className="text-base xl:text-lg text-[#F7F5F0]/80 max-w-md leading-relaxed">
                        Nossa Conciergerie Digital est├í pronta para criar o site e a identidade visual perfeita para voc├¬s.
                    </p>
                </motion.div>

                {/* Testimonial */}
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.4 }}
                    className="relative z-10 bg-[#F7F5F0]/5 backdrop-blur-md p-6 xl:p-8 rounded-2xl border border-[#F7F5F0]/10"
                >
                    <div className="flex gap-1 mb-4 text-[#C19B58]">
                        {[1, 2, 3, 4, 5].map(i => <Sparkles key={i} size={14} fill="currentColor" />)}
                    </div>
                    <p className="text-base xl:text-lg font-serif italic mb-4">"Adorei a facilidade. S├│ preenchi o briefing e a equipe me chamou no WhatsApp com tudo pronto."</p>
                    <div className="flex items-center gap-4">
                        <div className="w-10 h-10 rounded-full bg-[#C19B58]/20 flex items-center justify-center text-[#C19B58] font-bold">
                            C
                        </div>
                        <div>
                            <p className="font-medium text-[#F7F5F0]">Camila & Jorge</p>
                            <p className="text-xs text-[#F7F5F0]/60 uppercase tracking-wider">Noivos LUMA</p>
                        </div>
                    </div>
                </motion.div>
            </div>

            {/* Right Column - Form */}
            <div className="relative h-full flex items-center justify-center p-6 bg-noise overflow-hidden">
                {/* Leaf Shadow on light side too */}
                <div className="absolute inset-0 pointer-events-none overflow-hidden">
                    <div className="absolute top-0 right-0 w-[500px] h-[500px] opacity-10 rotate-90">
                        <Image src="/images/assets/leaf-shadow.png" alt="Shadow" fill className="object-contain" />
                    </div>
                </div>

                <div className="w-full max-w-md space-y-6 lg:space-y-8 relative z-10 scale-95 lg:scale-100 origin-center">
                    <div className="text-center lg:text-left">
                        <h2 className="text-3xl font-medium text-[#2A3B2E] mb-2 font-[family-name:var(--font-heading)]">
                            {isLogin ? "├ürea do Casal" : "Iniciar Projeto"}
                        </h2>
                        <p className="text-[#6B7A6C]">
                            {isLogin ? "Acesse para ver o andamento." : "Preencha para liberar seu briefing."}
                        </p>
                    </div>

                    <motion.form
                        key={isLogin ? "login" : "register"}
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        exit={{ opacity: 0, x: -20 }}
                        className="space-y-4 xl:space-y-6"
                        onSubmit={handleAuth}
                    >
                        {!isLogin && (
                            <div className="grid grid-cols-2 gap-4">
                                <div className="space-y-2">
                                    <label className="text-xs font-bold text-[#2A3B2E] uppercase tracking-wider">Nome</label>
                                    <input type="text" className="w-full px-4 py-3 rounded-lg border border-[#DCD3C5] bg-white/50 focus:border-[#C19B58] focus:ring-1 focus:ring-[#C19B58] outline-none transition-all text-[#2A3B2E]" placeholder="Seu nome" />
                                </div>
                                <div className="space-y-2">
                                    <label className="text-xs font-bold text-[#2A3B2E] uppercase tracking-wider">WhatsApp</label>
                                    <input type="tel" className="w-full px-4 py-3 rounded-lg border border-[#DCD3C5] bg-white/50 focus:border-[#C19B58] focus:ring-1 focus:ring-[#C19B58] outline-none transition-all text-[#2A3B2E]" placeholder="(11) 99999-9999" />
                                </div>
                            </div>
                        )}

                        <div className="space-y-2">
                            <label className="text-xs font-bold text-[#2A3B2E] uppercase tracking-wider">E-mail</label>
                            <input type="email" className="w-full px-4 py-3 rounded-lg border border-[#DCD3C5] bg-white/50 focus:border-[#C19B58] focus:ring-1 focus:ring-[#C19B58] outline-none transition-all text-[#2A3B2E]" placeholder="exemplo@email.com" />
                        </div>

                        <div className="space-y-2">
                            <label className="text-xs font-bold text-[#2A3B2E] uppercase tracking-wider">Senha</label>
                            <input type="password" className="w-full px-4 py-3 rounded-lg border border-[#DCD3C5] bg-white/50 focus:border-[#C19B58] focus:ring-1 focus:ring-[#C19B58] outline-none transition-all text-[#2A3B2E]" placeholder="ÔÇóÔÇóÔÇóÔÇóÔÇóÔÇóÔÇóÔÇó" />
                        </div>

                        <button
                            type="submit"
                            className="w-full bg-[#2A3B2E] text-[#F7F5F0] py-3.5 xl:py-4 rounded-lg font-medium tracking-wide hover:bg-[#1f2d22] transition-all flex items-center justify-center gap-2 group shadow-lg shadow-[#2A3B2E]/20"
                        >
                            {isLogin ? "Acessar Projeto" : "Continuar para o Briefing"}
                            <ArrowRight size={18} className="group-hover:translate-x-1 transition-transform" />
                        </button>
                    </motion.form>

                    <div className="relative">
                        <div className="absolute inset-0 flex items-center">
                            <div className="w-full border-t border-[#DCD3C5]/50"></div>
                        </div>
                        <div className="relative flex justify-center text-sm">
                            <span className="px-2 bg-[#F7F5F0] text-[#6B7A6C]">Ou entre com</span>
                        </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <button className="flex items-center justify-center gap-2 py-3 border border-[#DCD3C5] bg-white/50 rounded-lg hover:bg-white hover:border-[#C19B58] transition-colors text-sm font-medium text-[#3E4A3F]">
                            Google
                        </button>
                        <button className="flex items-center justify-center gap-2 py-3 border border-[#DCD3C5] bg-white/50 rounded-lg hover:bg-white hover:border-[#C19B58] transition-colors text-sm font-medium text-[#3E4A3F]">
                            Apple
                        </button>
                    </div>

                    <p className="text-center text-sm text-[#6B7A6C]">
                        {isLogin ? "Ainda n├úo iniciou?" : "J├í tem um projeto em andamento?"}{" "}
                        <button
                            onClick={() => setIsLogin(!isLogin)}
                            className="text-[#2A3B2E] font-medium hover:underline hover:text-[#C19B58] transition-colors"
                        >
                            {isLogin ? "Criar novo" : "Fazer login"}
                        </button>
                    </p>
                </div>
            </div>
        </div>
    );
}

"use client";

import { UseFormReturn } from "react-hook-form";
import { Heart, MapPin, Calendar, Clock } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { BriefingFormData } from "@/lib/briefingSchema";

interface StepCoupleEventProps {
    form: UseFormReturn<BriefingFormData>;
}

export function StepCoupleEvent({ form }: StepCoupleEventProps) {
    const { register, formState: { errors } } = form;

    return (
        <div className="space-y-8 animate-fade-in">
            <div className="text-center mb-10">
                <h2 className="text-3xl text-forest font-heading mb-2">
                    O Casal e o Evento
                </h2>
                <p className="text-muted-foreground font-light italic">
                    Conte-nos sobre a ess├¬ncia da uni├úo de voc├¬s
                </p>
            </div>

            <div className="grid md:grid-cols-2 gap-8">
                {/* Nome da Noiva */}
                <div className="space-y-3">
                    <Label htmlFor="brideName" className="flex items-center gap-2 text-forest/80">
                        <span className="text-gold text-lg">Ô£ª</span> Nome da Noiva
                    </Label>
                    <Input
                        id="brideName"
                        placeholder="Ex: Maria Clara"
                        {...register("brideName")}
                        className={errors.brideName ? "border-red-500/50" : ""}
                    />
                    {errors.brideName && (
                        <p className="text-xs text-red-500 font-medium ml-1">{errors.brideName.message}</p>
                    )}
                </div>

                {/* Nome do Noivo */}
                <div className="space-y-3">
                    <Label htmlFor="groomName" className="flex items-center gap-2 text-forest/80">
                        <span className="text-gold text-lg">Ô£ª</span> Nome do Noivo
                    </Label>
                    <Input
                        id="groomName"
                        placeholder="Ex: Jo├úo Pedro"
                        {...register("groomName")}
                        className={errors.groomName ? "border-red-500/50" : ""}
                    />
                    {errors.groomName && (
                        <p className="text-xs text-red-500 font-medium ml-1">{errors.groomName.message}</p>
                    )}
                </div>
            </div>

            <div className="w-full h-px bg-gradient-to-r from-transparent via-[#DCD3C5] to-transparent my-6 opacity-50" />

            <div className="grid md:grid-cols-2 gap-8">
                {/* Data do Casamento */}
                <div className="space-y-3">
                    <Label htmlFor="weddingDate" className="flex items-center gap-2 text-forest/80">
                        <Calendar className="w-4 h-4 text-gold" /> Data do Grande Dia
                    </Label>
                    <Input
                        id="weddingDate"
                        type="date"
                        {...register("weddingDate")}
                        className={errors.weddingDate ? "border-red-500/50" : ""}
                    />
                    {errors.weddingDate && (
                        <p className="text-xs text-red-500 font-medium ml-1">{errors.weddingDate.message}</p>
                    )}
                </div>

                {/* Hor├írio */}
                <div className="space-y-3">
                    <Label htmlFor="weddingTime" className="flex items-center gap-2 text-forest/80">
                        <Clock className="w-4 h-4 text-gold" /> Hor├írio
                    </Label>
                    <Input
                        id="weddingTime"
                        type="time"
                        {...register("weddingTime")}
                        className={errors.weddingTime ? "border-red-500/50" : ""}
                    />
                    {errors.weddingTime && (
                        <p className="text-xs text-red-500 font-medium ml-1">{errors.weddingTime.message}</p>
                    )}
                </div>
            </div>

            <div className="grid md:grid-cols-2 gap-8">
                {/* Nome do Local */}
                <div className="space-y-3">
                    <Label htmlFor="venueName" className="flex items-center gap-2 text-forest/80">
                        <MapPin className="w-4 h-4 text-gold" /> Nome do Local
                    </Label>
                    <Input
                        id="venueName"
                        placeholder="Ex: Fazenda Vista Verde"
                        {...register("venueName")}
                        className={errors.venueName ? "border-red-500/50" : ""}
                    />
                    {errors.venueName && (
                        <p className="text-xs text-red-500 font-medium ml-1">{errors.venueName.message}</p>
                    )}
                </div>

                {/* Endere├ºo */}
                <div className="space-y-3">
                    <Label htmlFor="venueAddress" className="flex items-center gap-2 text-forest/80">
                        <MapPin className="w-4 h-4 text-gold opaque-0" /> Endere├ºo Completo
                    </Label>
                    <Input
                        id="venueAddress"
                        placeholder="Rodovia ou Link do Google Maps"
                        {...register("venueAddress")}
                        className={errors.venueAddress ? "border-red-500/50" : ""}
                    />
                    {errors.venueAddress && (
                        <p className="text-xs text-red-500 font-medium ml-1">{errors.venueAddress.message}</p>
                    )}
                </div>
            </div>
        </div>
    );
}

"use client";

import { UseFormReturn } from "react-hook-form";
import { Wallet, Building, User, Key, Info, Smartphone, Mail, FileText, Shuffle, Check } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { BriefingFormData, pixKeyTypes } from "@/lib/briefingSchema";
import { cn } from "@/lib/utils";

interface StepPixDataProps {
    form: UseFormReturn<BriefingFormData>;
}

function PixTypeCard({
    selected,
    onClick,
    icon: Icon,
    label
}: {
    selected: boolean,
    onClick: () => void,
    icon: any,
    label: string
}) {
    return (
        <div
            onClick={onClick}
            className={cn(
                "relative cursor-pointer rounded-lg border transition-all duration-200 overflow-hidden flex flex-col items-center justify-center p-4 gap-2 hover:scale-[1.02]",
                selected
                    ? "border-[#C19B58] bg-[#F7F5F0] shadow-md ring-1 ring-[#C19B58]"
                    : "border-neutral-200 bg-white shadow-sm hover:border-[#C19B58]/50"
            )}
        >
            <Icon className={cn("w-6 h-6", selected ? "text-[#C19B58]" : "text-gray-400")} />
            <span className={cn("text-xs font-bold uppercase tracking-wider", selected ? "text-[#2A3B2E]" : "text-gray-500")}>
                {label}
            </span>
            {selected && (
