"use client";

import { UseFormReturn, Controller } from "react-hook-form";
import { Palette, Type, Sliders, Check } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
    BriefingFormData,
    weddingStyles,
