                </Button>
            </div>

        </div>
    );
}

"use client";

import { Edit } from "lucide-react";

interface EditableImageProps {
    src: string;
    alt: string;
    field: string;
    className?: string;
}

export default function EditableImage({ src, alt, field, className }: EditableImageProps) {
    const handleClick = (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        window.parent.postMessage({
            type: "REQUEST_IMAGE_CHANGE",
            field
        }, "*");
    };

    return (
        <div
            className={`relative group cursor-pointer ${className}`}
            onClick={handleClick}
        >
            <img src={src} alt={alt} className="w-full h-full object-cover" />

            {/* Overlay on hover */}
            <div className="absolute inset-0 bg-black/0 group-hover:bg-black/30 transition-colors flex items-center justify-center">
                <div className="opacity-0 group-hover:opacity-100 bg-white/90 p-2 rounded-full shadow-lg transition-opacity">
                    <Edit size={20} className="text-gray-700" />
                </div>
            </div>
        </div>
    );
}

"use client";

import { useEffect, useRef } from "react";

interface EditableTextProps {
    value: string;
    field: string;
    className?: string;
    tag?: "h1" | "h2" | "p" | "span" | "div";
}

export default function EditableText({ value, field, className, tag = "span" }: EditableTextProps) {
    const ref = useRef<HTMLElement>(null);

    const handleBlur = () => {
        if (ref.current) {
            const newValue = ref.current.innerText;
            if (newValue !== value) {
                window.parent.postMessage({
                    type: "UPDATE_DATA",
                    field,
                    value: newValue
                }, "*");
            }
        }
    };

    // Keep manual sync if prop changes from outside (e.g. sidebar input)
    useEffect(() => {
        if (ref.current && ref.current.innerText !== value) {
            ref.current.innerText = value;
        }
    }, [value]);

    const Tag = tag as any;

    return (
        <Tag
            ref={ref}
            contentEditable
            suppressContentEditableWarning
            onBlur={handleBlur}
            className={`outline-none focus:ring-2 focus:ring-[var(--gold)]/50 focus:bg-white/10 rounded px-1 transition-all cursor-text empty:before:content-['...'] ${className}`}
        >
            {value}
        </Tag>
    );
}

